mapper.insert:
INSERT INTO GD.CALENDAR
(
	CALD_ID, CALD_START,
	CALD_END, CALD_COLOR
)
VALUES
(
	CALENDAR_SEQ.NEXTVAL, TO_DATE(#{cald_start}, 'YYYY/MM/DD HH24:MI'),
	TO_DATE(#{cald_end}, 'YYYY/MM/DD HH24:MI'),#{cald_color}
)

mapper.update:
UPDATE ENTRBOARD
	SET EBOARD_CONTENT = #{eboard_content}
	WHERE CALD_ID=#{cald_id}
	AND CGORY_NO=302

mapper.delete:
DELETE FROM CALENDAR c 
			WHERE CALD_ID = #{cald_id}
			AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MI')<=TO_CHAR(CALD_END,'YYYYMMDDHH24MI')
